import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';

function RecipePage() {
    const location = useLocation();
    const [recipes, setRecipes] = useState([]);
    const [selectedRecipeIndex, setSelectedRecipeIndex] = useState(0); // Track selected recipe index

    useEffect(() => {
        const searchParams = new URLSearchParams(location.search);
        const query = searchParams.get('query');

        const fetchRecipes = async () => {
            try {
                const appId = 'a9c9e6ea'; // Replace with your actual Edamam app ID
                const appKey = '9a527fdf5250a160dadc31aa0b6919ae'; // Replace with your actual Edamam app key
                const searchUrl = `https://api.edamam.com/search?q=${encodeURIComponent(query)}&app_id=${appId}&app_key=${appKey}`;
                const response = await fetch(searchUrl);
                const data = await response.json();
                setRecipes(data.hits);
            } catch (error) {
                console.error('Failed to fetch recipes:', error);
            }
        };

        if (query) {
            fetchRecipes();
        }
    }, [location.search]);

    const handleImageClick = (index) => {
        setSelectedRecipeIndex(index); // Set selected recipe index
    };

    return (
        <div className="container-fluid py-5 mt-5">
            <div className="container py-5">
                <div className="row g-4 mb-5">
                    <div className="col-lg-8 col-xl-9">
                        {/* Render details of the selected recipe */}
                        <div className="row g-4">
                            <div className="col-lg-6">
                                <div className="border rounded">
                                    <a href="#">
                                        <img src={recipes[selectedRecipeIndex]?.recipe.image} alt={recipes[selectedRecipeIndex]?.recipe.label} className="img-fluid rounded" />
                                    </a>
                                </div>
                            </div>
                            <div className="col-lg-6">
                                <h4 className="fw-bold mb-3">{recipes[selectedRecipeIndex]?.recipe.label}</h4>
                                <p className="mb-3">Category: Vegetables</p>
                                <h5 className="fw-bold mb-3">3,35 $</h5>
                            
                                <ul>
                                    {recipes[selectedRecipeIndex]?.recipe.ingredientLines.map((ingredient, i) => ( 
                                        <li className="mb-4" key={`${recipes[selectedRecipeIndex]?.recipe.uri}-ingredient-${i}`}>{ingredient}</li>
                                    ))}
                                </ul>
                                <div className="input-group quantity mb-5" style={{ width: '100px' }}>
                                    <div className="input-group-btn">
                                        <button className="btn btn-sm btn-minus rounded-circle bg-light border">
                                            <i className="fa fa-minus"></i>
                                        </button>
                                    </div>
                                </div>
                                <a href="#" className="btn border border-secondary rounded-pill px-4 py-2 mb-4 text-primary">
                                    <i className="fa fa-shopping-bag me-2 text-primary"></i> Add to cart
                                </a>
                                {/* Display diet labels, health labels, and nutrient details */}
                                <div className="mb-4">
                                    <p className="fw-bold">Diet Labels:</p>
                                    <p>{recipes[selectedRecipeIndex]?.recipe.dietLabels.join(', ')}</p>
                                    <p className="fw-bold">Health Labels:</p>
                                    <p>{recipes[selectedRecipeIndex]?.recipe.healthLabels.join(', ')}</p>
                                    <p className="fw-bold">Total Nutrients:</p>
                                    <ul>
                                        {Object.entries(recipes[selectedRecipeIndex]?.recipe.totalNutrients).map(([nutrient, details]) => (
                                            <li key={nutrient}>{nutrient}: {details.label} ({details.quantity} {details.unit})</li>
                                        ))}
                                    </ul>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="container-fluid py-5 mt-5">
                        <h1 className="fw-bold mb-0">Related products</h1>
                        <div className="vesitable">
                            <div className="owl-carousel vegetable-carousel justify-content-center">
                                {recipes.map(({ recipe }, index) => (
                                    index !== selectedRecipeIndex && (
                                        <div key={recipe.uri} className="border border-primary rounded position-relative vesitable-item">
                                            <div className="vesitable-img">
                                                <img src={recipe.image} className="img-fluid w-100 rounded-top" alt={recipe.label} onClick={() => handleImageClick(index)} />
                                            </div>
                                            <div className="text-white bg-primary px-3 py-1 rounded position-absolute" style={{ top: '10px', right: '10px' }}>Vegetable</div>
                                            <div className="p-4 pb-0 rounded-bottom">
                                                <h4>{recipe.label}</h4>
                                                <p>Lorem ipsum dolor sit amet consectetur adipisicing elit sed do eiusmod te incididunt</p>
                                                <div className="d-flex justify-content-between flex-lg-wrap">
                                                    <p className="text-dark fs-5 fw-bold">$4.99 / kg</p>
                                                    <a href="#" className="btn border border-secondary rounded-pill px-3 py-1 mb-4 text-primary">
                                                        <i className="fa fa-shopping-bag me-2 text-primary"></i> Add to cart
                                                    </a>
                                                </div>
                                            </div>
                                        </div>
                                    )
                                ))}
                            </div>
                        </div>
                    </div>

                </div>
            </div>
        </div>
    );
}

export default RecipePage;
