import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

const BlogPosts = () => {
    const [recipes, setRecipes] = useState([]);
    const staticPosts = [
        {
            id: 1,
            title: '10 Tips for a Healthier Lifestyle',
            image: 'path/to/your/image1.jpg',
            snippet: 'Discover simple and effective tips to lead a healthier lifestyle...',
            url: '/blog/healthier-lifestyle'
        },
        {
            id: 2,
            title: 'Top 5 Superfoods You Should Include in Your Diet',
            image: 'path/to/your/image2.jpg',
            snippet: 'Learn about the top 5 superfoods and their benefits...',
            url: '/blog/superfoods'
        },
        {
            id: 3,
            title: 'How to Meal Prep for a Week',
            image: 'path/to/your/image3.jpg',
            snippet: 'A guide to meal prepping for a stress-free week...',
            url: '/blog/meal-prep'
        }
    ];

    useEffect(() => {
        const fetchRecipes = async () => {
            const appId = 'your-app-id'; // Replace with your Edamam app ID
            const appKey = 'your-app-key'; // Replace with your Edamam app key
            const query = 'healthy'; // Change this to any query you prefer

            const response = await axios.get(`https://api.edamam.com/search?q=${query}&app_id=${appId}&app_key=${appKey}`);
            setRecipes(response.data.hits);
        };

        fetchRecipes();
    }, []);

    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 3,
        slidesToScroll: 1,
    };

    return (
        <div className="container my-5">
            <h2 className="text-center">Food and Healthy Lifestyle Blog</h2>
            <Slider {...settings}>
                {staticPosts.map(post => (
                    <div key={post.id} className="p-3">
                        <div className="card blog-card">
                            <div className="card_image">
                                <img src={post.image} alt={post.title} />
                            </div>
                            <div className="card_content">
                                <h5 className="card_title">{post.title}</h5>
                                <p className="card_text">{post.snippet}</p>
                                <a href={post.url} className="btn btn-primary">Read More</a>
                            </div>
                        </div>
                    </div>
                ))}
                {recipes.map(({ recipe }) => (
                    <div key={recipe.uri} className="p-3">
                        <div className="card blog-card">
                            <div className="card_image">
                                <img src={recipe.image} alt={recipe.label} />
                            </div>
                            <div className="card_content">
                                <h5 className="card_title">{recipe.label}</h5>
                                <p className="card_text">{recipe.ingredientLines.slice(0, 5).join(', ')}...</p>
                                <a href={recipe.url} target="_blank" rel="noopener noreferrer" className="btn btn-primary">Read More</a>
                            </div>
                        </div>
                    </div>
                ))}
            </Slider>
        </div>
    );
};

export default BlogPosts;
