import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';

function RecipePage() {
    const location = useLocation();
    const [recipes, setRecipes] = useState([]);

    useEffect(() => {
        const searchParams = new URLSearchParams(location.search);
        const query = searchParams.get('query');

        const fetchRecipes = async () => {
            try {
                const appId = 'a9c9e6ea'; // Replace with your actual Edamam app ID
                const appKey = '9a527fdf5250a160dadc31aa0b6919ae'; // Replace with your actual Edamam app key
                const searchUrl = `https://api.edamam.com/search?q=${encodeURIComponent(query)}&app_id=${appId}&app_key=${appKey}`;
                const response = await fetch(searchUrl);
                const data = await response.json();
                setRecipes(data.hits);
            } catch (error) {
                console.error('Failed to fetch recipes:', error);
            }
        };

        if (query) {
            fetchRecipes();
        }
    }, [location.search]);

    const handleImageClick = (index) => {
        const clickedRecipe = recipes[index].recipe;
        console.log('Clicked recipe:', clickedRecipe);
        // Navigate to details page with recipe ID or other identifier
        // Example: window.location.href = `/recipe/${clickedRecipe.id}`;
    };

    return (
        <div>
            <h1>Recipes</h1>
            {recipes.length > 0 && (
                <div>
                    <h2>{recipes[0].recipe.label}</h2>
                    <img src={recipes[0].recipe.image} alt={recipes[0].recipe.label} style={{ width: '100px', height: '100px' }} />
                    <p>Click on the image for details</p>
                </div>
            )}
            <div className="recipe-images">
                {recipes.slice(1).map(({ recipe }, index) => (
                    <img 
                        key={recipe.uri} 
                        src={recipe.image} 
                        alt={recipe.label} 
                        style={{ width: '100px', height: '100px', cursor: 'pointer' }} 
                        onClick={() => handleImageClick(index + 1)} // index + 1 to account for the top recipe
                    />
                ))}
            </div>
        </div>
    );
}

export default RecipePage;
