import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';

function RecipePage() {
    const location = useLocation();
    const [recipes, setRecipes] = useState([]);
    const [selectedRecipeIndex, setSelectedRecipeIndex] = useState(0); // Track clicked recipe index

    useEffect(() => {
        const searchParams = new URLSearchParams(location.search);
        const query = searchParams.get('query');

        const fetchRecipes = async () => {
            try {
                const appId = 'a9c9e6ea'; // Replace with your actual Edamam app ID
                const appKey = '9a527fdf5250a160dadc31aa0b6919ae'; // Replace with your actual Edamam app key
                const searchUrl = `https://api.edamam.com/search?q=${encodeURIComponent(query)}&app_id=${appId}&app_key=${appKey}`;
                const response = await fetch(searchUrl);
                const data = await response.json();
                setRecipes(data.hits);
            } catch (error) {
                console.error('Failed to fetch recipes:', error);
            }
        };

        if (query) {
            fetchRecipes();
        }
    }, [location.search]);

    const handleImageClick = (index) => {
        setSelectedRecipeIndex(index); // Set clicked recipe index
    };

    return (
        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}> {/* Flexbox container */}
            <div style={{ width: '70%', textAlign: 'left', marginBottom: '20px' }}> {/* Details container */}
                {recipes.map(({ recipe }, index) => (
                    <div key={recipe.uri} style={{ display: index === selectedRecipeIndex ? 'block' : 'none' }}>
                        <h2>{recipe.label}</h2>
                        <img src={recipe.image} alt={recipe.label} style={{ width: '200px', height: '200px', float: 'left', marginRight: '20px' }} />
                        <div>
                            <p>Ingredients:</p>
                            <ul>
                                {recipe.ingredientLines.map((ingredient, index) => (
                                    <li key={index}>{ingredient}</li>
                                ))}
                            </ul>
                            <p>Diet Labels: {recipe.dietLabels.join(', ')}</p>
                            <p>Health Labels: {recipe.healthLabels.join(', ')}</p>
                            <p>Total Nutrients:</p>
                            <ul>
                                {Object.entries(recipe.totalNutrients).map(([nutrient, details]) => (
                                    <li key={nutrient}>{nutrient}: {details.label} ({details.quantity} {details.unit})</li>
                                ))}
                            </ul>
                            <a href={recipe.url} target="_blank" rel="noopener noreferrer">View Recipe</a>
                        </div>
                    </div>
                ))}
            </div>
            <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center' }}> {/* Images container */}
                {recipes.map(({ recipe }, index) => (
                    <div key={recipe.uri} style={{ margin: '10px', textAlign: 'center' }}>
                        <img
                            src={recipe.image}
                            alt={recipe.label}
                            style={{ width: '100px', height: '100px', cursor: 'pointer' }}
                            onClick={() => handleImageClick(index)}
                        />
                    </div>
                ))}
            </div>
        </div>
    );
}

export default RecipePage;
