export async function fetchRecipes(query) {
    if (!query.trim()) {
        throw new Error('Please enter at least one ingredient.');
    }

    const appId = 'a9c9e6ea'; // Replace 'YOUR_EDAMAM_APP_ID' with your actual Edamam app ID
    const appKey = '9a527fdf5250a160dadc31aa0b6919ae'; // Replace 'YOUR_EDAMAM_APP_KEY' with your actual Edamam app key
    const searchUrl = `https://api.edamam.com/search?q=${encodeURIComponent(query)}&app_id=${appId}&app_key=${appKey}`;


    try {
        const response = await fetch(searchUrl);
        const data = await response.json();
        return data.hits;
    } catch (error) {
        console.error(error);
        throw new Error('Failed to fetch recipes. Please try again later.');
    }
}
